
public class Ex03_10 {

  public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a=100, b=200;
    float result;
    result=(float)a/b;
    System.out.printf("%f \n", result);
    
    float result1;
    result1=1+2.0f;
    System.out.printf("%f \n", result1);
    
    float result2;
    result2=(float)2/3;
    System.out.printf("%f \n", result2);
    /**
     * 정수끼리나누면 결과가정수값이므로
     * 결과값을 실수로처리하도록 정해줌.
     **/
  
    float result3;
    result3=(float)10/3;
    System.out.printf("%f \n", result3);
    
    float result4;
    result4=10.0f/3;
    System.out.printf("%f \n", result4);
    
    System.out.printf("%c %c %c \n", 48, 65, 97);
  }

}
